class Solution {
public:
    long long countSubarrays(vector<int>& arr, int k) {
        int n = arr.size();
        long long ans = 0;
        unordered_map<long long, long long> prev, curr;

        for (int i = 0; i < n; ++i) {
            curr.clear();
            if (arr[i] == k) ++ans;

            curr[arr[i]] = 1;
            for (auto& [val, freq] : prev) {
                long long temp = val & arr[i];
                if (temp == k) {
                    ans += freq;
                }
                curr[temp] += freq;
            }
            prev = curr;
        }
        return ans;
    }
};
