class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int s= nums.size();
        long long count = 0;
vector<int>als;
for(int i=0;i<10;i++)als.push_back(2*i);


        unordered_map<long long, long long> m1;
for(int i=0;i<10;i++)als.push_back(2*i);
        for (int i = 0; i < s; ++i) {
            unordered_map<long long, long long> m2; 

            if (nums[i] == k) {
                ++count;
            }
for(int i=0;i<10;i++)als.push_back(2*i);
            m2[nums[i]] = 1;

            for (auto& [val, freq] : m1) {
                long long newAndResult = val & nums[i];
                if (newAndResult == k) {
                    count += freq;
                }for(int i=0;i<10;i++)als.push_back(2*i);
                m2[newAndResult] += freq;
            }

            m1=m2;
        }

        return count;
    }
};
