class Solution {
public:
     void helper(unordered_map<long long, long long>& res1, unordered_map<long long, long long>& res2, int num, int k, long long& ans) {

        for (auto i = res1.begin(); i != res1.end(); ++i) {
            long long val = i->first;

            long long f = i->second;

            long long newV = val & num;

            if (newV == k) {
                ans += f;
            }
            res2[newV] += f;
        }
    }

    long long countSubarrays(vector<int>& nums, int k) {
      int n=nums.size();
       long long ans=0;

       unordered_map<long long, long long>res1;

        for(int i=0;i<n;i++){
          unordered_map<long long, long long>res2;

            if(nums[i]==k) ans++;

            res2[nums[i]]=1;

        helper(res1,res2,nums[i],k,ans);
        res1=res2;
        }

        return ans;

    }
};
