#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        ll ans=0;
        vector<int> v(34,0);
        map<ll,ll> mp1;
        int last=0;
        int n=nums.size();
        for(int i=0;i<n;i++)
        {
            map<ll,ll> mp2;
            if(nums[i]==k) ans++;

            mp2[nums[i]]=1;

            for(auto it:mp1)
            {
                if((it.first&nums[i])==k) ans+=it.second;
                mp2[it.first&nums[i]]+=it.second;
            }
            mp1.clear();
            mp1=mp2;
        }
        return ans;
    }
};
