class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        map<int,long>possibleValues;
        long ans = 0;
        for(auto&x:nums){
            map<int,long>curr;
            curr[x] = 1;
            for(auto&val:possibleValues){ 
                curr[x&val.first] += val.second;
            } 
            ans += curr[k];
            possibleValues = curr;
        }
        return ans;
    }
};
