#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        ll n = nums.size();
        ll ans = 0;
        ll right = 0;
        map<ll,ll> pr;
        for(int i = 0 ; i < n ; i++){
            map<ll,ll> cr;
            cr[nums[i]] = 1;
            for(auto it : pr){
                ll nar = it.first & nums[i];
                if(nar == k){
                    ans += it.second;
                }
                cr[nar] += it.second;
            }
            pr = cr;
            if(nums[i] == k) ans++;
        }
        return ans;
    }
};
