class Solution {
public:
    typedef long long ll;
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        ll ans = 0;
        unordered_map<ll, ll>mp1;
        for(int i=0; i<n; i++) {
            unordered_map<ll, ll>mp2; 
            if (nums[i]==k) ans++;

            mp2[nums[i]] = 1;
            for(auto it:mp1){
                ll num = it.first;
                ll freq = it.second;
                ll res = num&nums[i];
                if(res == k) {
                    ans += freq;
                }
                mp2[res] += freq;
            }

            mp1 = mp2;
        }
        return ans;
    }
};
