void fio(){
    ios::sync_with_stdio(false); // Speeds up input/output
    cin.tie(nullptr); // Unties cin from cout for faster input
}

class Solution {
public:
    ll countSubarrays(vi& num, int tx) {
        fio();
        int x = num.size();
        ll ttl = 0;
        ump<ll, ll> previousResults;
        for (int idx = 0; idx < x; ++idx) {
            ump<ll, ll> curr_RES; 
            if (num[idx] == tx) {
                ++ttl;
            }
            curr_RES[num[idx]] = 1;
            for (auto& [value, frequency] : previousResults) {
                ll new__res = value & num[idx];
                if (new__res == tx) {
                    ttl += frequency;
                }
                curr_RES[new__res] += frequency;
            }

            previousResults = curr_RES;
        }
        return ttl;
    }
};
