class Solution {
public:
    long long countSubarrays(vector<int>& a, int k) {
        int n = a.size();
        map<lli, lli> mp;
        lli ans = 0;

        for (int i = 0; i < n; i++) {
            map<lli, lli> cur;
            if (a[i] == k) ans++;
            cur[a[i]]++;
            for (auto &j : mp) {
                lli x = j.first & a[i];                                                                               
                if (x == k) {
                    ans += j.second;
                }
                cur[x] += j.second;
            }
            mp = cur;
        }
        return ans;
    }
};
