class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long c=0;
        unordered_map<long long,long long>prev;
        for(int i=0;i<nums.size();i++){
            unordered_map<long long,long long>curr;
            if(nums[i]==k) c++;

            curr[nums[i]]=1;

            for(auto &it:prev){
                long long newRes= it.first & nums[i];
                if(newRes==k) c+=it.second;

                curr[newRes]+=it.second;
            }
            prev=curr;

        }
        return c;
    }
};
