class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int arraySize = nums.size();
        long long totalCount = 0;

        unordered_map<long long, long long> previousRes;

        int currentIndex = 0;
        while (currentIndex < arraySize) {
            unordered_map<long long, long long> currentRes; 

            if (nums[currentIndex] == k) {
                totalCount++;
            }
            currentRes[nums[currentIndex]] = 1;

            auto it = previousRes.begin();
            while (it != previousRes.end()) {
                long long currentVal = it->first;
                long long frequency = it->second;
                long long newAndResult = currentVal & nums[currentIndex];
                if (newAndResult == k) {
                    totalCount += frequency;
                }
                currentRes[newAndResult] += frequency;
                it++;
            }

            previousRes = currentRes;
            ++currentIndex;
        }

        return totalCount;
    }
};
