class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        vector<vector<int>> v(n,vector<int> (32,0));
        for(int i=0;i<n;i++){
            for(int j=0;j<32;j++){
                v[i][j]=(nums[i]>>j)&1;
            }
        }
        for(int i=0;i<32;i++){
            for(int j=1;j<n;j++){
                v[j][i] = v[j-1][i]*v[j][i] + v[j][i];       
            }
        }
        long long count=0;
        for(int i=0;i<n;i++){
            int mini=i+1,maxi=0;
            for(int j=0;j<32;j++){
                if(((k>>j)&1)==1)
                {
                    mini = min(mini,v[i][j]);
                }
                else{
                    maxi = max(maxi,v[i][j]);
                }
            }
            //cout<<maxi<<" "<<mini<<endl;
            count+=max(0,mini-maxi);
        }

        // for(auto ele:v){
        //     for(auto ele1:ele){
        //         cout<<ele1<<" ";
        //     }
        //     cout<<endl;
        // }

        return count;
    }
};
