class Solution {
public:
    long long countSubarrays(vector<int>& a, int b) {
        int c = a.size();
        long long d = 0;

        unordered_map<long long, long long> e;

        for (int f = 0; f < c; ++f) {
            unordered_map<long long, long long> g; 

            bool currentIsK = (a[f] == b);
            if (currentIsK) {
                ++d;
            }
            g[a[f]] = 1;

            for (auto& [h, i] : e) {
                long long j = h & a[f];
                bool newAndResultIsK = (j == b);
                if (newAndResultIsK) {
                    d += i;
                }
                g[j] += i;
            }

            e = g;
        }

        return d;
    }
};
