class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k)
    {
        long long ans=0;
        unordered_map<long long , long long>mp;
        for(int i=0; i<nums.size(); i++)
        {
            if(nums[i] == k)
            {
                ans++;
            }
            unordered_map<long long , long long>cur;
            cur[nums[i]]=1;
            for(auto [val , freq] : mp)
            {
                long long res=val&nums[i];
                if (res==k)
                {
                    ans+=freq;
                }
                cur[res]+=freq;
            }
            mp=cur;
        }
        return ans;
    }
};
