class Solution {
    public long countSubarrays(int[] nums, int k) {

       int n = nums.length;
        long count = 0;

        HashMap<Long, Long> prev = new HashMap<>();

        for (int i = 0; i < n; i++) {

            HashMap<Long, Long> curr = new HashMap<>();

            if (nums[i] == k) {
                count++;
            }
            curr.put((long) nums[i], 1L); 

            for (Map.Entry<Long, Long> entry : prev.entrySet()) {
                long val = entry.getKey();
                long freq = entry.getValue();
                long newAndResult = val & nums[i];
                if (newAndResult == k) {
                    count += freq;
                }
                curr.put(newAndResult, curr.getOrDefault(newAndResult, 0L) + freq);
            }

            prev = curr;
        }

        return count;
    }
}
