class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n = len(nums)
        seen_values = {}
        res = 0

        for num in nums:
            temp = {}
            if num == k:
                res += 1

            temp[num] = 1

            for val, freq in seen_values.items():
                and_val = num & val
                if and_val == k:
                    res += freq
                if and_val in temp:
                    temp[and_val] += freq
                else:
                    temp[and_val] = freq

            seen_values = temp

        return res
