class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        ans = 0
        left = [-1] * 31
        for i, x in enumerate(nums):
            l, r = -1, i
            for bit in range(31):
                if not (x >> bit) & 1:
                    left[bit] = i
                if (k >> bit) & 1:
                    l = max(l, left[bit])
                else:
                    r = min(r, left[bit])
            ans += max(0, r - l)
        return ans
