class Solution {
    func countSubarrays(_ nums: [Int], _ k: Int) -> Int {
        let size = nums.count
        var total: Int64 = 0
        var previousResults: [Int64: Int64] = [:]

        var index = 0
        while index < size {
            var currentResults: [Int64: Int64] = [:]

            if nums[index] == k {
                total += 1
            }
            currentResults[Int64(nums[index])] = 1

            for (value, frequency) in previousResults {
                let newAndResult = value & Int64(nums[index])
                if newAndResult == k {
                    total += frequency
                }
                currentResults[newAndResult, default: 0] += frequency
            }

            previousResults = currentResults
            index += 1
        }
        return Int(total)
    }
}
