class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        cnt = dict()
        ans = 0
        for i, x in enumerate(nums):
            new_cnt = dict()
            for ands, left in cnt.items():
                new_and = ands & x
                if new_and in new_cnt:
                    new_cnt[new_and] += left
                else:
                    new_cnt[new_and] = left    
            if x in new_cnt:
                new_cnt[x] += 1
            else:
                new_cnt[x] = 1
            cnt = new_cnt
            for ands, left in cnt.items():
                if ands == k:
                    ans += left
        return ans
