class Solution {
public:
    long long countSubarrays(vector<int>& a, int k) {
        using ll = long long;
        using umi = unordered_map <ll,ll>;
        ll ct=0;
        auto f=[&](int x,umi &m1){
            umi m2;
            m2[x]=1;
            for(auto it:m1){
                if((x&it.first)==k)ct+=it.second;
                m2[(x&it.first)]+=it.second;
            }
           return m2;
        };
        umi m1;
        for(auto &x:a){
            if(x==k)ct++;
            m1= f(x,m1);
        }
        return ct;
    }
};
