class Solution {
public:
    long long countSubarrays(vector<int>& b, int k) {
        long long ans=0;
        int si=b.size();
        unordered_map<int,long long> mp;
        set<pair<int,int>> se;
        int j=0;
        for(auto it : b){
            se.insert({it, j});
            j++;
        }

        for(int i=0;i<si;i++){
            unordered_map<int,long long> mpp;
            for(auto it : mp){
                int a = it.first&b[i];
                mpp[a]+=it.second;

                if(a==k){
                    ans+=it.second;
                }
            }

            mpp[b[i]]++;

            mp=mpp;

            if(b[i]==k)ans++;
        }

        return ans;
    }
};
