class Solution {
public:
    long long countSubarrays(vector<int>& a, int k) {
        long long res = 0;
        unordered_map<long long, long long> prev;
        for (int i = 0; i < a.size(); i++) {
            unordered_map<long long, long long> cur; 
            if (a[i] == k) {
                res++;
            }
            cur[a[i]] = 1;
            for (const auto& [val, freq] : prev) {
                long long newVal = val & a[i];
                if (newVal == k) {
                    res += freq;
                }
                cur[newVal] += freq;
            }
            prev = cur;
        }
        return res;
    }
};
