# class Solution:
#     def countSubarrays(self, nums: List[int], k: int) -> int:
from collections import defaultdict

class Solution:
    def countSubarrays(self, nums, k):
        n = len(nums)
        count = 0
        prev_counts = defaultdict(int)

        for num in nums:
            curr_counts = defaultdict(int)

            if num == k:
                count += 1

            curr_counts[num] += 1

            for prev_num, freq in prev_counts.items():
                new_and_result = prev_num & num
                if new_and_result == k:
                    count += freq
                curr_counts[new_and_result] += freq

            prev_counts = curr_counts

        return count
