class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans = 0;
        unordered_map<long long, long long> previousAndCount;

        for (int num : nums) {
            unordered_map<long long, long long> currentAndCount;
            currentAndCount[num]++;

            for (auto& [prevAnd, prevCount] : previousAndCount) {
                long long newAnd = prevAnd & num;
                currentAndCount[newAnd] += prevCount;
            }

            if (currentAndCount.find(k) != currentAndCount.end()) {
                ans += currentAndCount[k];
            }

            previousAndCount = currentAndCount;
        }

        return ans;
    }
};
