#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        ll ans = 0;
        unordered_map<ll, ll > pr;
        for (int i = 0; i < n; i++) {
            unordered_map<ll, ll> cr; 
            if (nums[i] == k) {
                ans++;
            }
            cr[nums[i]] = 1;
            for (auto& [val, fr] : pr) {
                long long nr = val & nums[i];
                if (nr == k) {
                    ans += fr;
                }
                cr[nr] += fr;
            }

            pr = cr;
        }
        return ans;
    }
};
