class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0;
        int n = nums.size();
        vector<pair<int, int>>ands;
        for (int i = n - 1; i >= 0; i--) {
            ands.push_back({ -1,i });
            ands[0].first &= nums[i];
            int p = 0;
            for (int j = 1; j < ands.size(); j++) {
                ands[j].first &= nums[i];
                if (ands[j].first == ands[p].first) {
                    ands[p].second = ands[j].second;
                }
                else ands[++p] = ands[j];
            }
            ands.resize(p + 1);
            for (int j = 0; j < ands.size(); j++) {
                if (ands[j].first == k) {
                    if (j==0)ans+=n-ands[j].second;
                    else ans += ands[j - 1].second - ands[j].second;
                }
            }
        }
        return ans;
    }
};
