class Solution 
{
public:
    long long countSubarrays(vector<int>& nums, int k) 
    {
        long long ans = 0;
        unordered_map<int, long long> probval;

        for (int num : nums)
        {
            unordered_map<int, long long> new_probval;

            for (auto& prev : probval)
            {
                int res = prev.first & num;
                new_probval[res] += prev.second;
                if (res == k)
                {
                    ans += prev.second;
                }
            }

            if (num == k)
            {
                ans++;
            }

            new_probval[num]++;

            probval = new_probval;
        }
        return ans;
    }
};
