#define ll long long
#define F first
#define S second
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        ll n = nums.size(),ans = 0;
        map<ll, ll> mp;
        for (ll i = 0; i < n; i++)
        {
            map<ll, ll> mp1;
            if (nums[i] == k)
                ans++;
            mp1[nums[i]] = 1;
            for (auto it : mp)
            {
                ll temp = it.first & nums[i];
                if (temp == k)
                    ans += it.second;
                mp1[temp] += it.second;
            }
            mp = mp1;
        }
        return ans;      

    }
};
