class Solution {
    using ll = long long;
public:
    long long countSubarrays(vector<int>& nums, int k) {
        ll ans = 0;
        unordered_map<int, int> cnt;
        for (int& x : nums) {
            unordered_map<int, int> tcnt;
            for (auto& [f, s] : cnt)
                tcnt[f & x] += s;
            ++tcnt[x];
            for (auto& [f, s] : tcnt)
                if (f == k)
                    ans += s;
            cnt = tcnt;
        }
        return ans;
    }
};
