class Solution {
public:
    #define ll long long
    #define vi vector<int>
    #define pb push_back
    long long countSubarrays(vector<int>& a, int k) {
        int n = a.size();
        ll ans=0;
        vector<ll>arr(n);
        for(int i=0; i<n; i++){
            arr[i] = a[i];
        }
        vector<ll>b(40);
        for(int i=0; i<40; i++){
            b[i] = -1;
        }
        for(int i=0; i<n; i++)
        {
            ll x = k;
            vi a1;
            vi a0;
            for(int j=0; j<40; j++){
                if((1ll<<j)&arr[i]){
                    //
                }
                else{
                    b[j] = i;
                }
            }
            for(int j=0; j<40; j++){
                if((1ll<<j)&x){
                    a1.pb(b[j]);
                }
                else{
                    a0.pb(b[j]);
                }
            }
            sort(a1.rbegin(), a1.rend());
            sort(a0.begin(), a0.end());
            int z1 = a1.size();
            int z0 = a0.size();
            if(z0==0){
                ans += (i-a1[0]);
            }
            else if(z1==0){
                ans += (i+1);
                ans -= (i-a0[0]);
            }
            else{
                if(a0[0]<= a1[0]){
                    continue;
                }
                ans += (i-a1[0]);
                ans -= (i-a0[0]);
            }
        }
        return ans;
    }
};
