class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();

        long long res=0;

        map<long, long>mp;


        for(int i=0;i<n;i++){
            map<long, long> cur;
            if(k==nums[i])res++;

            cur[nums[i]]=1;
            for(auto it:mp){
                long long newr=it.first&nums[i];
                if(k==newr)res+=it.second;

                cur[newr]+=it.second;
            }
            mp=cur;

        }
        return res;
    }
};
