class Solution {
public:
    long long countSubarrays(vector<int>& arr, int k) {
        int s = arr.size();
        long long t = 0;
        unordered_map<long long ,long long >p;
        for(int idx = 0; idx< s; idx++){
            unordered_map<long long ,long long >c;
            if(arr[idx] == k) ++t;
            c[arr[idx]] = 1;
            for(auto&[value,freq]: p){
                long long n = value & arr[idx];
                if(n == k) t+= freq;
                c[n]+= freq;
            }
            p = c;
        }


        return t;

    }
};
