#define ll long long

class Solution {
public:
    long long countSubarrays(vector<int> &v, int k) {
        int n = v.size();
        ll ans = 0;
        unordered_map<int, ll> m;
        for(int i = 0; i < n; i++){
            unordered_map<int, ll> mp; 
            if(v[i] == k){
                ++ans;
            }
            mp[v[i]] = 1;
            for(auto &x : m){
                int curr = x.first & v[i];
                if(curr == k){
                    ans += x.second;
                }
                mp[curr] += x.second;
            }
            m = mp;
        }
        return ans;
    }
};
