class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n = len(nums)
        count = 0
        h1 = {}
        for i in range(n):
            h2 = {}
            if nums[i] == k:
                count += 1
            h2[nums[i]] = h2.get(nums[i], 0) + 1
            for key in h1:
                new = key & nums[i]
                if new == k:
                    count += h1[key]
                h2[new] = h2.get(new, 0) + h1[key]
            h1 = h2
        return count
