class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0LL;
        int n = nums.size();
        for (int i = 0; i < n; i++) {
            int j = i-1;
            while (j >= 0 && (nums[j]&nums[i]) != nums[j]) {
                nums[j] &= nums[i];
                j--;
            }
            ans += upper_bound(nums.begin(), nums.begin()+i+1, k)-lower_bound(nums.begin(), nums.begin()+i+1, k);
        }
        return ans;
    }
};
