class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
         int n = nums.size();
         long long count = 0;
    unordered_map<long long, long long> and_count;

    for (int num : nums) {
        unordered_map<long long, long long> new_and_count;
        new_and_count[num]++;
        for (auto& [key, value] : and_count) {
            int new_and = key & num;
            new_and_count[new_and] += value;
        }
        if (new_and_count.find(k) != new_and_count.end()) {
            count += new_and_count[k];
        }
        and_count = new_and_count;
    }

    return count;
    }
};
