class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        unordered_map<int,long long> mp1,mp2;       
        long long ans=0;
        int i=0;
        while(i<n){
            mp2[nums[i]]=1;
            for(auto it:mp1){
                int x=it.first&nums[i];
                mp2[x]+=it.second;
                if(x==k) ans+=it.second;
            }
            if(nums[i]==k) ans++;
            mp1=mp2;
            mp2.clear();
            i++;
        }
        return ans;
    }
};
