static auto fastio = []()
{
    std::ios_base::sync_with_stdio(false);
    std::cin.tie(nullptr);
    std::cout.tie(nullptr);
};

class Solution
{
public:
    long long countSubarrays(vector<int> &nums, int k)
    {
        fastio();
        int n = nums.size();
        long long counter = 0;
        unordered_map<long long, long long> hash;
        for (int i = 0; i < n; ++i)
        {
            unordered_map<long long, long long> hash1;

            if (nums[i] == k)
            {
                ++counter;
            }
            hash1[nums[i]] = 1;

            for (auto &ii : hash)
            {
                long long value = ii.first;
                long long freq = ii.second;
                long long result = value & nums[i];
                if (result == k)
                {
                    counter += freq;
                }
                hash1[result] += freq;
            }
            hash = hash1;
        }
        return counter;
    }
};
