class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0;
        unordered_map<long long, long long> finalMap, tempMap;
        for(int i = 0; i < nums.size(); i++) {
            if(nums[i] == k) ans++;
            tempMap[nums[i]] = 1;
            for(auto it: finalMap) {
                int rem = (it.first & nums[i]);
                if(rem == k) {
                    ans += it.second;
                }
                tempMap[rem] += it.second;
            }
            finalMap = tempMap;
            tempMap.clear();
        }
        return ans;
    }
};
