class Solution {
public:
    long long countSubarrays(vector<int>& v, int k) {
        int n = v.size();
        long long count = 0;
        map<long long, long long> mp;
        for (auto &x:v) {
            map<long long, long long> sha; 
            if (x == k) {
                ++count;
            }
            sha[x] = 0;
sha[x]++;
            for (auto& [val, freq] : mp) {
                long long cnt = val & x;
                if (cnt == k) {
                    count += freq;
                }
                sha[cnt] += freq;
            }
            mp = sha;
        }
        return count;
    }
};
