class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {


           int n=nums.size();
        vector<vector<int>>pr(n+1,vector<int>(32,0));

        for(int i=0;i<n;i++){
            for(int j=0;j<32;j++){
                int bit=(nums[i]&(1<<j))>0;

                pr[i+1][j]=pr[i][j]+bit;
            }
        }


        // minimum possible diff 

        long long mindiff=0ll;

        for(int i=0;i<=n-1;i++){


            int st=i;
            int en=n-1;
            int ans=INT_MAX;

            while(st<=en){
                int mid=st+(en-st)/2;
                long long no=0ll;
                int len=mid-i+1;

                // cout<<st<<" "<<en

                for(int j=0;j<32;j++){
                    long long c=pr[mid+1][j]-pr[i][j];
                    if(c==len)no=no|(1<<j);
                }

                // cout<<mid<<" "<<no<<endl;

               // cout<<no<<endl;

                if(no==k){
                    ans=mid;
                    en=mid-1;

                }

                else if(no<k){

                    en=mid-1;
                }
                else{
                    st=mid+1;
                }

            }

         //   cout<<ans<<endl;


            st=i;
            en=n-1;
            int ans1=INT_MAX;

            while(st<=en){
                int mid=st+(en-st)/2;
                long long no=0ll;
                int len=mid-i+1;

                for(int j=0;j<32;j++){
                    long long c=pr[mid+1][j]-pr[i][j];
                    if(c==len)no=no|(1<<j);
                }

                if(no==k){
                    ans1=mid;
                    st=mid+1;
                }

                else if(no<k){
                    en=mid-1;
                }
                else{
                    st=mid+1;
                }
            }

            if(ans!=INT_MAX){
                int len=ans1-ans+1;
                mindiff+=len;
            }


           //  cout<<ans1<<endl;


        }

        return mindiff;




    }
};
