class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n = len(nums)

        res = 0

        mp = {}

        for i in range(n):
            temp = {}
            if nums[i] == k:
                res += 1
            temp[nums[i]] = 1

            for num, val in mp.items():

                curr = num & nums[i]

                if curr == k:
                    res += val

                if curr in temp:
                    temp[curr] += val

                else:
                    temp[curr] = val

            mp = temp

        return res
