class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        long long ans=0;
        vector<vector<int>> a(n+1,vector<int>(32,0)); 
        for(int i=1;i<=n;i++){
            a[i]=a[i-1];
            int temp=nums[i-1];
            for(int j=0;j<31;j++){
                if(temp%2==0){
                    a[i][j]=i;
                }
                temp/=2;
                // cout<<a[i][j]<<" ";
            }
            // cout<<endl;

            temp=k;
            int max1=0,min0=i;
            for(int j=0;j<31;j++){
                if(temp&1){
                    max1=max(max1,a[i][j]);
                }
                else{
                    min0=min(min0,a[i][j]);
                }
                temp/=2;
            }

            ans+=(max(0,min0-max1));
        }

        return ans;
    }
};
