class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size(); 
        long long cnt = 0; 
        unordered_map<long long, long long>mpp; 

        for (int i = 0; i < n; i++) { 
            unordered_map<long long, long long> mpp2; 
            if (nums[i] == k) { 
                cnt++; 
            } 
            mpp2[nums[i]] = 1; 
            for (auto& [val, freq] : mpp) { 
                long long nar = val & nums[i]; 
                if (nar == k) { 
                    cnt += freq; 
                } 
                mpp2[nar] += freq; 
            }  
            mpp = mpp2; 
        } 
        return cnt;
    }
};
