class Solution {
public:
    long long countSubarrays(vector<int>& array, int target) {
        int n = array.size();
        long long totalSubarrays = 0;
        unordered_map<long long, long long> prefixCounts;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> currentPrefixCounts; 
            if (array[i] == target) {
                ++totalSubarrays;
            }
            currentPrefixCounts[array[i]] = 1;
            for (auto& [prevValue, prevFrequency] : prefixCounts) {
                long long newAndResult = prevValue & array[i];
                if (newAndResult == target) {
                    totalSubarrays += prevFrequency;
                }
                currentPrefixCounts[newAndResult] += prevFrequency;
            }

            prefixCounts = currentPrefixCounts;
        }
        return totalSubarrays;
    }
};
