class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int l = nums.size();
        long long res = 0;

        unordered_map<long long, long long> prevs;

        for (int i = 0; i < l; ++i) {
            unordered_map<long long, long long> currs; 

            if (nums[i] == k) {
                ++res;
            }
            currs[nums[i]] = 1;

            for (auto& [val, freq] : prevs) {
                long long newresult = val & nums[i];
                if (newresult == k) {
                    res += freq;
                }
                currs[newresult] += freq;
            }

            prevs = currs;
        }

        return res;
    }
};
