class Solution {
public:
    #define pii pair<int,int>
#define all(arr) arr.begin(),arr.end()
#define fr first
#define sc second
#define pb push_back
        #define ll long long
        long long countSubarrays(vector<int>& arr, int k) {
            int n= arr.size();
            map<int,ll>feasible;
            ll ans=0;
            for(int i=n-1; i>=0; i--){
                map<int,ll>newFeasible;
                newFeasible[arr[i]]++;
                for(auto &ele: feasible){
                    newFeasible[(ele.fr&(arr[i]))]+= ele.sc;
                }
                ans+= newFeasible[k];
                feasible= newFeasible;
            }
            return ans;
        }
    };
