#define ll long long
class Solution {
public:
    ll countSubarrays(vector<int>& nums, int target) {
        ll n = nums.size();
        ll ans = 0;
        map<ll, ll> prev;
        for (ll i = 0; i < n; i++) {
            map<ll, ll> curr; 
            ans+=(nums[i]==target);
            curr[nums[i]] = 1;
            for (auto& x : prev) {
                ll nextAnd = x.first & nums[i];
                if (nextAnd == target) {
                    ans += x.second;
                }
                curr[nextAnd] += x.second;
            }

            prev = curr;
        }
        return ans;
    }
};
