typedef long long ll;
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        ll cnt = 0;
        unordered_map<ll,ll>mp;
        for (int i = 0; i<n;i++) 
        {
            unordered_map<ll,ll>mp2; 
            if (nums[i] ==k) 
            {
                cnt++;
            }
            mp2[nums[i]]=1;
            for (auto j :mp) 
            {
                if ((j.first & nums[i])==k) 
                {
                    cnt += j.second;
                }
                mp2[(j.first & nums[i]) ]+=j.second;
            }
            mp = mp2;
        }

        return cnt;
    }
};
