import java.util.HashMap;
import java.util.Map;

class Solution {
    public long countSubarrays(int[] arr, int target) {
        int n = arr.length;
        long total = 0;
        Map<Long, Long> prev = new HashMap<>();

        for (int i = 0; i < n; ++i) {
            Map<Long, Long> curr = new HashMap<>();

            if (arr[i] == target) {
                ++total;
            }

            curr.put((long) arr[i], 1L);

            for (Map.Entry<Long, Long> entry : prev.entrySet()) {
                long andResult = entry.getKey() & arr[i];

                if (andResult == target) {
                    total += entry.getValue();
                }

                curr.put(andResult, curr.getOrDefault(andResult, 0L) + entry.getValue());
            }

            prev = curr;
        }

        return total;
    }
}
